import React, { useEffect, useState } from 'react';
import './AddImage.css';
import Navbar from '../Home/Navbar/Navbar';
import logo from '../../images/icon.png'
import { storage } from '../../firebase';
import { useForm } from "react-hook-form";
import axios from 'axios';


const UploadImage = () => {
  const [image, setImage] = useState(null);
  const [url, setUrl] = useState("");
  const [progress, setProgress] = useState(0);
  const [imageURL, setImageURL] = useState();
  var storageRef = storage.ref();
  const { register, handleSubmit } = useForm();



  const handleChange = e => {
    if (e.target.files[0]) {
      setImage(e.target.files[0]);
    }
  };

  const handleImageUpload = event => {
    console.log(event?.target?.files[0]);
    const imageData = new FormData();
    imageData.set('key', '47b2d957da970efd46650889d3040352');
    imageData.append('image', event?.target.files[0]);

    axios.post('https://api.imgbb.com/1/upload', imageData)
        .then(function (response) {
            setImageURL(response?.data.data.display_url);
        })
        .catch(function (error) {
            console.log(error);
        });
}

const onSubmit = data => {
    const eventData = {
        image: url,
    };
    const url = `http://localhost:5000/addService`
    console.log(eventData);
    fetch(url, {
        method: 'POST',
        headers: {
            'content-type': 'application/json'
        },
        body: JSON.stringify(eventData)
    })
        .then(res => console.log('server side res', res))
};

  const handleUpload = () => {
    const uploadTask = storage.ref(`images/${image.name}`).put(image);
    uploadTask.on(
      "state_changed",
      snapshot => {
        const progress = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        setProgress(progress);
      },
      error => {
        console.log(error);
      },
      () => {
        storage
          .ref("images")
          .child(image.name)
          .getDownloadURL()
          .then(url => {
            setUrl(url);
          });
      }
    );
  };

  const handleDelete = () => {
    var desertRef = storageRef.child(`images/${image.name}`);
    desertRef.delete().then(() => {
      alert('file deleted')
    }).catch((error) => {
      
    });
  }

  console.log("url: ", url);

  return (
    <section>
      <Navbar />
      <div style={{ position: "relative", marginTop: '50px' }} className="addImage">
        <div
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)"
          }}
          className="container text-center">


        </div>
        {/* <form  className="container">
          <div class="form-row">
            <div class="input-group mb-3 col-6 btn btn-outline-dark">
              <div class="form-group">
                <input onChange={handleChange} type="file" class="form-control-file" />
              </div>

            </div>

            <div class="col-1 ">
              <button onClick={handleUpload} type="button" class="btn btn-primary p-3">Upload</button>
            </div>


          </div>
        </form> */}
        <form onSubmit={handleSubmit(onSubmit)}>
                                <h3>Add Your Service Details Here</h3>
                                {/* <input name="name" defaultValue="Service Name" {...register("name")} /> */}
                                <br />
                                {/* <input name="price" defaultValue="Service Price" {...register("price")} /> */}

                                <br />
                                <input type="file" name="exampleRequired"  onChange={handleChange} />

                                <br />
                                <input onClick={handleUpload} className="btn btn-primary  text-white" type="submit" />
                            </form>

        <div class=" my-3 container text-center">
          <p> Image URL: {url} </p>
          <img src={url} class="card-img-top text-center" alt="..." style={{ width: '30%', height: 'auto' }} />
          {/* <div class="card-body">
                            <h5 class="card-title">Card title</h5>
                            <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                            <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
                        </div> */}
          <div class="input-group my-3 w-50 text-center">
            <input type="text" class="form-control" placeholder="Image Title" aria-label="Image Title" aria-describedby="button-addon2" />

          </div>
        </div>
      </div>
    </section>
  );
};

export default UploadImage;
