import React, { useEffect, useState } from 'react';
import './AddImage.css';
import Navbar from '../Home/Navbar/Navbar';
import logo from '../../images/icon.png'
import { storage } from '../../firebase';


const AddImage = () => {
  const [image, setImage] = useState(null);
  const [url, setUrl] = useState("");
  const [progress, setProgress] = useState(0);
  var storageRef = storage.ref();


// const pustFetch = () => {
//   fetch('http://localhost:5000/events', {
//     method: 'POST',
//     headers: {
//       'Content-Type': 'application/json'
//     },
//     body: JSON.stringify({ name: 'mijan', age: 36 })
//   })
//   console.log('click')
// }


  const handleChange = e => {
    if (e.target.files[0]) {
      setImage(e.target.files[0]);
    }
  };



  const handleUpload = () => {
    const uploadTask = storage.ref(`images/${image.name}`).put(image);
    uploadTask.on(
      "state_changed",
      snapshot => {
        const progress = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        setProgress(progress);
      },
      error => {
        console.log(error);
      },
      () => {
        storage
          .ref("images")
          .child(image.name)
          .getDownloadURL()
          .then(url => {
            setUrl(url);
          });
      }
    );
    

  };

  const handleDelete = () => {
    var desertRef = storageRef.child(`images/${image.name}`);
    desertRef.delete().then(() => {
      alert('file deleted')
    }).catch((error) => {
      
    });
  }

  console.log("url: ", url);


  const formPart = () => {
    const gettingFormPart = document.getElementById('uploadImage');
    const hidingImagePart = document.getElementById('imagePart');
    // hidingImagePart.style.display = 'none';
    hidingImagePart.style.display = 'block';

  }

  const imagePart = () => {

  }

  return (
    <section>
      <Navbar />
      <div style={{ position: "relative", marginTop: '50px' }} className="addImage">
        <div
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)"
          }}
          className="container text-center">


        </div>
        <form id="uploadImage" className="container">
          <div class="form-row">
            <div class="input-group mb-3 col-6 btn btn-outline-dark">
              <div class="form-group">
                <input onChange={handleChange} type="file" class="form-control-file" />
              </div>

            </div>

            <div class="col-1 ">
              <button onClick={handleUpload} type="button" class="btn btn-primary p-3">Upload</button>
            </div>
            {/* <div class="col-1 ">
              <button onClick={handleDelete} type="button" class="btn btn-primary p-3">Delete</button>
            </div> */}


            <div class="col-2 progress ml-5 progress">
              <progress class="progress-bar" value={progress} max="100" />
            </div>
            <div className="col-2">
            <button onClick={handleDelete} class="btn btn-outline-primary py-3" type="button"> Delete </button>
            </div>
          </div>
        </form>

        <div style={{display: 'none'}} id="imagePart" class=" my-3 container text-center">
          <p> Image URL: {url} </p>
          <img src={url} class="card-img-top text-center" alt="..." style={{ width: '30%', height: 'auto' }} />
          {/* <div class="card-body">
                            <h5 class="card-title">Card title</h5>
                            <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                            <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
                        </div> */}
          <div class="input-group my-3 w-50 text-center">
            <input type="text" class="form-control" placeholder="Image Title" aria-label="Image Title" aria-describedby="button-addon2" />
            <div class="input-group-append">
              <button class="btn btn-outline-primary" type="button" id="button-addon2">Button</button>
              {/* <button onClick={pustFetch} class="btn btn-outline-primary" type="button" id="button-addon2">PustFetch</button> */}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default AddImage;
