import React, { useState } from 'react';
import './AddImage.css';
import Navbar from '../Home/Navbar/Navbar';
import logo from '../../images/icon.png'
import { storage,  } from '../firebase/firebase.config';
import { getStorage, ref } from "firebase/storage";

const AddImage = () => {
  const storage = getStorage();

// Points to the root reference
const storageRef = ref(storage);
  const [image, setImage] = useState(null);
  const [url, setUrl] = useState("");
  const [progress, setProgress] = useState(0);

  const handleChange = e => {
    if (e.target.files[0]) {
      setImage(e.target.files[0]);
    }
  };

  const handleUpload = () => {
    // const imagesRef = ref(storageRef, 'images');
    const uploadTask = storage.ref(`images/${image.name}`).put(image);
    uploadTask.on(
      "state_changed",
      snapshot => {
        const progress = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        setProgress(progress);
      },
      error => {
        console.log(error);
      },
      () => {
        storage
          .ref("images")
          .child(image.name)
          .getDownloadURL()
          .then(url => {
            setUrl(url);
          });
      }
    );
  };

  console.log("image: ", image);

  return (
    <section>
      <Navbar />
      <div style={{ position: "relative", marginTop: '50px' }} className="addImage">
        <div
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)"
          }}
          className="container text-center">
       

        </div>
        <form className="container">
          <div class="form-row">
            {/* <div class="col-6">
                                <input type="file" class="form-control" placeholder="City" />
                            </div> */}
            <div class="input-group mb-3 col-6 btn btn-outline-dark">
              <div class="form-group">
                <input onChange type="file" class="form-control-file" id="exampleFormControlFile1" />
              </div>
              
            </div>

            <div class="col-1 ">
            <button onClick={handleUpload} type="button" class="btn btn-primary p-3">Upload</button>
            </div>


            {/* <div class="col-4 progress">
                            <div class="progress-bar progress-bar-striped bg-primary" role="progressbar" style={{ width: '100%', height: '100px' }} aria-valuenow="100" aria-valuemin="0" aria-valuemax="100"></div>
                        </div> */}
          </div>
        </form>

        <div class=" my-3 container text-center">
          <img src={url} class="card-img-top text-center" alt="..." style={{ width: '30%' }} />
          {/* <div class="card-body">
                            <h5 class="card-title">Card title</h5>
                            <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                            <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
                        </div> */}
          <div class="input-group my-3 w-50 text-center">
            <input type="text" class="form-control" placeholder="Image Title" aria-label="Image Title" aria-describedby="button-addon2" />
            <div class="input-group-append">
              <button class="btn btn-outline-primary" type="button" id="button-addon2">Button</button>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default AddImage;
