import React, { useEffect, useState } from 'react';
import './AddImage.css';
import Navbar from '../Home/Navbar/Navbar';
import logo from '../../images/icon.png'
import { storage } from '../../firebase';
import axios from 'axios';


const UploadImage = () => {
  const [image, setImage] = useState(null);
  const [url, setUrl] = useState("");
  const [progress, setProgress] = useState(0);
  const [imageURL, setImageURL] = useState();
  var storageRef = storage.ref();



  const handleChange = e => {
    if (e.target.files[0]) {
      setImage(e.target.files[0]);
    }
  };


const onSubmit = data => {
    const eventData = {
        imageURL: imageURL,
    };
    const url = `https://protected-wildwood-79219.herokuapp.com/addService`
    console.log(eventData);
    fetch(url, {
        method: 'POST',
        headers: {
            'content-type': 'application/json'
        },
        body: JSON.stringify(eventData)
    })
        .then(res => console.log('server side res', res))
};

  const handleUpload = event => {
    console.log(event?.target?.files[0]);
    // const imageData = new FormData();
    const uploadTask = storage.ref(`images/${image.name}`).put(image);
    uploadTask.on(
      "state_changed",
      snapshot => {
        const progress = Math.round(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        setProgress(progress);
      },
      error => {
        console.log(error);
      },
      () => {
        storage
          .ref("images")
          .child(image.name)
          .getDownloadURL()
          .then(url => {
            setUrl(url);
          });
      }
    );
  };

  const handleDelete = () => {
    var desertRef = storageRef.child(`images/${image.name}`);
    desertRef.delete().then(() => {
      alert('file deleted')
    }).catch((error) => {
      
    });
  }

  console.log("url: ", url);

  return (
    <section>
      <Navbar />
      <div style={{ position: "relative", marginTop: '50px' }} className="addImage">
        <div
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)"
          }}
          className="container text-center">


        </div>
        <form onSubmit={onSubmit} className="container">
          <div class="form-row">
            <div class="input-group mb-3 col-6 btn btn-outline-dark">
              <div class="form-group">
                <input onChange={handleImageUpload} type="file" class="form-control-file" />
              </div>

            </div>

            <div class="col-4 p-3 ">
              <input type="text" />
            </div>
            {/* <div class="col-1 ">
              <button onClick={handleDelete} type="button" class="btn btn-primary p-3">Delete</button>
            </div> */}


          </div>
        </form>

        <div class=" my-3 container text-center">
          <p> Image URL: {url} </p>
          <img src={url} class="card-img-top text-center" alt="..." style={{ width: '30%', height: 'auto' }} />
          {/* <div class="card-body">
                            <h5 class="card-title">Card title</h5>
                            <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                            <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
                        </div> */}
          <div class="input-group my-3 w-50 text-center">
            <input type="text" class="form-control" placeholder="Image Title" aria-label="Image Title" aria-describedby="button-addon2" />

          </div>
        </div>
      </div>
    </section>
  );
};

export default UploadImage;
