import React, { useContext, useEffect, useState } from 'react';
import { UserContext } from '../../App';
import logo from '../../images/icon.png';
import Navbar from '../Home/Navbar/Navbar';
import PublicImg from './PublicImg';

const Dashboard = () => {
    const [loggedInUser, setLoggedInUser] = useContext(UserContext);
    const [publicImg, setPublicImg] = useState([])
    const [privateImg, setPrivateImg] = useState([])

    useEffect(() => {
        fetch('http://localhost:3002/getCommunity?email=' + loggedInUser.email)
            .then(res => res.json())
            .then(data => setPublicImg(data))
    }, [loggedInUser.email])

    console.log("d", publicImg);

    return (
        <div className=''>
            <Navbar />
            <h1 className=" pt-5 text-center">Hi {loggedInUser.name} , You Have uploaded  {publicImg.length} Images....!!!!!</h1>
            <div className="row mt-5">
                <div className="col-md-6">
                    <h1>Public Images : {publicImg.length}</h1>
                    <div className="row d-flex justify-content-center">
                        {
                            publicImg.map(upload => <PublicImg upload={upload} ></PublicImg>)
                        }
                    </div>

                </div>
                <div className="col-md-6">
                    <h1>Private Images : {privateImg.length}</h1>
                </div>

            </div>
            {/* <div className="container text-center">
                <div className="row">
                    <div class="card my-4 text-center col-3 m-2" style={{ width: '18rem', }}>
                        <img src={logo} class="card-img-top mx-auto d-block" alt="..."
                            style={{ width: '50%' }} />
                        <div class="card-body">
                            <h5 class="card-title">Image Tittle</h5>
                            <a href="h" class="btn btn-primary">Delete</a>
                        </div>
                    </div>  
                </div>
            </div> */}
        </div>
    );
};

export default Dashboard;
