import React, { useState } from 'react';
import Swal from 'sweetalert2';
import { storage } from '../../firebase/index.js'

const PublicImg = (props) => {
    const [ui, setUi] = useState(false)
    var storageRef = storage.ref();
    const { _id, url, title, imageName } = props.upload
    const handleDelete = (id) => {
        fetch(`http://localhost:3002/deletePublic/${id}`, {
            method: 'DELETE'
        })
            .then(res => res.json())
            .then(result => {
                Swal.fire({
                    position: 'center',
                    icon: 'success',
                    title: 'Your image has been deleted',
                    showConfirmButton: false,
                    timer: 1500
                })
            })
        var desertRef = storageRef.child(`images/${imageName}`);
        desertRef.delete().then(() => {
            setUi(true)

        }).catch((error) => {
            console.log(error);
        });
        console.log(id)
    }


    return (
        // <div class="card my-4 text-center col-3 m-2" style={{ width: '18rem', }}>
        //     <img src={url} class="card-img-top mx-auto d-block" alt="..."
        //         style={{ width: '50%' }} />
        //     <div class="card-body">
        //         <h5 class="card-title">{title}</h5>
        //         <button onClick={() => handleDelete(_id)} class="btn btn-primary">Delete</button>
        //     </div>
        // </div>

        <>
            {!ui &&
                <div class="card bg-secondary mx-1 my-1 " style={{ width: '30%' }}>
                    <img src={url} class="card-img-top h-50" alt="..." />
                    <div class="card-body">
                        <h5 class="card-title text-light text-center">{title}</h5>

                    </div>
                    <div className="card-footer">
                        <button onClick={() => handleDelete(_id)} class="btn btn-danger btn-block">Delete</button>
                    </div>
                </div>

            }
        </>
    );
};

export default PublicImg;
