import React, { useContext, useEffect, useState } from 'react';
import './AddImage.css';
import Navbar from '../Home/Navbar/Navbar';
import logo from '../../images/icon.png'
import { storage } from '../../firebase';
import { useMemo } from 'react';
import { UserContext } from '../../App';


const AddImage = () => {
  const [loggedInUser, setLoggedInUser] = useContext(UserContext);
  const [title, setTitle] = useState('')
  const [image, setImage] = useState(null);
  const [url, setUrl] = useState("");
  const [progress, setProgress] = useState(0);
  var storageRef = storage.ref();

  const handleChange = e => {
    if (e.target.files[0]) {
      setImage(e.target.files[0]);
    }
  };

  const handleUpload = async () => {
    const uploadTask = storage.ref(`images/${image.name}`).put(image);
    uploadTask.on(
      "state_changed",
      snapshot => {
        const progress = Math.ceil(
          (snapshot.bytesTransferred / snapshot.totalBytes) * 100
        );
        setProgress(progress);
      },
      error => {
        console.log(error);
      },
      () => {
        storage
          .ref("images")
          .child(image.name)
          .getDownloadURL()
          .then((url) => {
            setUrl(url);
          });
      }
    );


    // fetch('http://localhost:3002/addPrivate', {
    //   method: 'POST',
    //   headers: {
    //     'Content-Type': 'application/json'
    //   },
    //   body: JSON.stringify({
    //     imageName: image?.name,
    //     url,
    //     email: 'ashrafulmijan5@gmail.com'
    //   })
    // })

    const gettingFormPart = document.getElementById('uploadImage');
    const hidingImagePart = document.getElementById('imagePart');
    gettingFormPart.style.display = 'none';
    hidingImagePart.style.display = 'block';
  };

  const handleDelete = () => {
    var desertRef = storageRef.child(`images/${image.name}`);
    desertRef.delete().then(() => {
      alert('file deleted')
    }).catch((error) => {
      console.log(error);
    });
    window.location.reload(false);
  }

  const handlePrivate = () => {
    fetch('http://localhost:3002/addPrivate', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        imageName: image.name,
        url,
        email: loggedInUser.email,
        title
      })
    })
    // window.location.reload(false);
  }

  const handleCommunity = () => {
    fetch('http://localhost:3002/addCommunity', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        imageName: image.name,
        url,
        email: loggedInUser.email,
        title
      })
    })
    al
    // window.location.reload(false);
  }

  console.log("url: ", url);


  return (
    <section>
      <Navbar />
      <div style={{ position: "relative", marginTop: '50px' }} className="addImage">
        <div
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)"
          }}
          className="container text-center">


        </div>
        <form id="uploadImage" className="container">
          <div class="form-row d-flex justify-content-center">
            <div style={{ padding: '0px' }} class="input-group mb-3 col-6 btn btn-outline-dark">
              <div class="form-group">
                <input onChange={handleChange} type="file" class="form-control-file" />
              </div>

            </div>

            <div class="col-1 ">
              <button style={{ padding: '11px' }} onClick={handleUpload} type="button" class="btn btn-primary">Upload</button>
            </div>
            {/* <div class="col-1 ">
              <button onClick={handleDelete} type="button" class="btn btn-primary p-3">Delete</button>
            </div> */}


            {/* <div class="col-2 progress ml-5 progress">
              <progress class="progress-bar" value={progress} max="100" />
            </div>
            <div className="col-2">

            </div> */}
          </div>
        </form>

        <div style={{ display: 'none' }} id="imagePart" class=" my-3 container text-center">
          <p> Image URL: {url} </p>
          <img src={url} class="card-img-top text-center" alt="..." style={{ width: '30%', height: 'auto' }} />
          {/* <div class="card-body">
                            <h5 class="card-title">Card title</h5>
                            <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                            <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
                        </div> */}
          <div className="row">
            <div class="input-group my-3 w-50 text-center col-10">
              <input onChange={(e) => { setTitle(e.target.value) }} type="text" class="form-control" placeholder="Image Title" aria-label="Image Title" aria-describedby="button-addon2" />
              <div class="input-group-append ">
                <button onClick={handlePrivate} class="btn btn-outline-primary" type="button" id="button-addon2">Add Private</button>
                {/* <button onClick={pustFetch} class="btn btn-outline-primary" type="button" id="button-addon2">PustFetch</button> */}
              </div>

              <button onClick={handleCommunity} class="btn btn-outline-primary ml-3 col-2" type="button"> To Community </button>

              <button onClick={handleDelete} class="btn btn-outline-primary ml-3 col-2" type="button"> Delete </button>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default AddImage;
